def newPair():
    global factory
    global pairs
    global webhook
    for x in range(pairs,factory.functions.allPairsLength().call()):
        pairs+=1
        print("NEW PAIR CREATED ")
        addrpgl=factory.functions.allPairs(x).call()
        pglc=w3.eth.contract(address=addrpgl,abi=abipgl)
        token0 = pglc.functions.token0().call()
        token1 = pglc.functions.token1().call()
        token0c=w3.eth.contract(address=token0,abi=abierc20)
        token1c=w3.eth.contract(address=token1,abi=abierc20)
        print(token0c.functions.name().call()+ " = " + token0)
        print(token1c.functions.name().call()+ " = " + token1)
        print("")
        aaa=discord.Embed(title="New Pair on Pangolin",color=14177041,description=token0c.functions.name().call() + " / " + token1c.functions.name().call()+"\n[Trade]"+ "(https://app.pangolin.exchange/#/swap?inputCurrency=" + token0 + "&outputCurrency=" + token1 + ")")
        aaa.set_author(name="Pangolin",url="https://app.pangolin.exchange",icon_url="https://pbs.twimg.com/profile_images/1361750928924303368/zsmWYqWb_200x200.jpg")
        webhook.send(embed=aaa)
    return

